import React, { useState } from "react";
import TinderCard from "react-tinder-card";
import "./TinderCards.css";

function TinderCards(){
    const [people, setPeople]= useState(
        [
            {
                name: "Elon Musk",
                url:"https://www.google.com/imgres?imgurl=https%3A%2F%2Fwww.biography.com%2F.image%2Fc_fill%252Ccs_srgb%252Cfl_progressive%252Ch_400%252Cq_auto%3Agood%252Cw_620%2FMTY2MzU3Nzk2OTM2MjMwNTkx%2Felon_musk_royal_society.jpg&imgrefurl=https%3A%2F%2Fwww.biography.com%2Fbusiness-figure%2Felon-musk&tbnid=kS2Pyss5UwSH2M&vet=12ahUKEwjGrYOEv-jsAhWVALkGHS5tCnwQMygBegUIARCeAQ..i&docid=h24P-DAcCzayvM&w=620&h=400&q=elon%20musk&ved=2ahUKEwjGrYOEv-jsAhWVALkGHS5tCnwQMygBegUIARCeAQ"
            },
            {
                name: "Jeff Bezos",
                url:"https://www.google.com/imgres?imgurl=https%3A%2F%2Fcomputerworld.com.br%2Fwp-content%2Fuploads%2F2020%2F01%2FEm-meio-a-investiga%25C3%25A7%25C3%25B5es-e-protestos-Bezos-anuncia-US-1-bilh%25C3%25A3o-para-%25C3%258Dndia.jpg&imgrefurl=https%3A%2F%2Fcomputerworld.com.br%2Fcarreira%2Fjeff-bezos-recomenda-9-livros-para-entender-de-negocios-e-1-para-entender-sobre-a-vida%2F&tbnid=W8wkxYqIv3JL8M&vet=12ahUKEwjOvaO5v-jsAhW-NLkGHRM9Cn4QMygBegUIARCaAQ..i&docid=CdfRJB7_jz5IMM&w=1200&h=856&itg=1&q=jeff%20bezos&ved=2ahUKEwjOvaO5v-jsAhW-NLkGHRM9Cn4QMygBegUIARCaAQ"
            }
        ]
    );
    const swiped =(direction, nameToDelete)=> {
        console.log("removing: " +nameToDelete);
    };
    const outOfFrame =(name) => {
        console.log(name + " left the screen!");
    };
    return (
        <div className="tinderCards">
            <div className="tinderCards__cardContainer">
                {people.map((person) => (
                    <TinderCard
                    className="swipe"
                        key={person.name}
                        preventSwipe={["up","down"]}
                        onSwipe={(dir) => swiped(dir, person.name)}
                        onCardLeftScreen={() => outOfFrame(person.name)}
                    >
                        <div
                        style={{ backgroundImage: 'url(${person.url})' }}
                        className="card">
                            <h3>{ person.name }</h3>
                        </div>
                    </TinderCard>
                ))}
            </div>
        </div>
    );
}

export default TinderCards;
